qemu-system-aarch64 -nographic -kernel Image -cpu host --enable-kvm -smp 2 -machine virt -drive file=rootfs.ext4,if=none,format=raw,id=hd0 -device virtio-blk-device,drive=hd0 -m 4G -append "rootwait nokaslr root=/dev/vda rw init=/sbin/init console=ttyAMA0 kvm-arm.mode=protected" -serial mon:stdio -dtb qemu-optee.dtb
